import React, { Component } from "react";

class FormularioCadastro extends Component {
  constructor() {
    super();
    this.titulo = "";
  }

  handleMudancaTitulo(evento) {
    this.titulo = evento.target.value;
    console.log(this.titulo);
  }

  render() {
    return (
      <form className="form-cadastro">
        <input type="text" placeholder="Titulo" className="form-cadastro_input" onChange={this.handleMudancaTitulo} />
        <textarea rows={15} placeholder="Escreva sua nota..." className="form-cadastro_input" />
        <button className="form-cadastro_input form-cadastro-submit">Criar nota</button>
      </form>
    );
  }
}

export default FormularioCadastro;
